    private void jTable1MousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable1MousePressed
        tb.setId(tableModel.list.get(jTable1.getSelectedRow()).getId());
        tb.setVisitante(tableModel.list.get(jTable1.getSelectedRow()).getVisitante());
        tb.setEmail(tableModel.list.get(jTable1.getSelectedRow()).getEmail());
        tb.setQuarto(tableModel.list.get(jTable1.getSelectedRow()).getQuarto());
        tb.setData_entrada(tableModel.list.get(jTable1.getSelectedRow()).getData_entrada());
        
        txtid.setText(String.valueOf(tb.getId()));
        txtnome.setText(tb.getVisitante());
        txtemail.setText(tb.getEmail());
        txtquarto.setText(String.valueOf(tb.getQuarto()));
        txtdata.setText(String.valueOf(tb.getData_entrada()));
    }//GEN-LAST:event_jTable1MousePressed

    private void jButton1MousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton1MousePressed
        rDAO.cancelReserva(tb);
    }//GEN-LAST:event_jButton1MousePressed

